from Bio.Blast import NCBIWWW, NCBIXML

program = "blastn"
sequence = ""

def launch_blast(sequence, program = "blastn", database = "nr", e_value_threshold = 10, word_size = 10, megablast = False):

    blast_output = NCBIWWW.qblast(program = "blastn", database = "nr", sequence = sequence,
                                format_type = "XML", descriptions = 0, alignments = 100,
                                expect = e_value_threshold, hitlist_size = 100, megablast = megablast, word_size = word_size)
    blast_entries = NCBIXML.read(blast_output)
    blast_entry_list = []
    for entry in blast_entries.alignments:
        blast_entry_list.append(entry.hsps[0])
    return blast_entry_list
